import React, { useState, useEffect, useContext } from 'react';
import { styled, Box, IconButton } from '@mui/material';
import { Favorite as FavoriteIcon } from '@mui/icons-material';

import { API } from '../../service/api';
import { DataContext } from '../../context/DataProvider';

const Container = styled(Box)(({ theme }) => ({
    margin: '50px 100px',
    [theme.breakpoints.down('md')]: {
        margin: 0
    }
}));

const Image = styled('img')({
    width: '100%',
    height: '50vh',
    objectFit: 'cover'
});

const DetailView = ({ post }) => {
    const { account } = useContext(DataContext);
    const [liked, setLiked] = useState(false);
    const [likes, setLikes] = useState(post.likes || 0); // Initialize likes count from the post data

    useEffect(() => {
        // Check if the current user has already liked this post
        if (account && post.likesBy.includes(account.username)) {
            setLiked(true);
        }
    }, [account, post.likesBy]);

    const handleLike = async () => {
        if (!liked) {
            // Send a request to like the post
            await API.likePost(post._id);
            setLikes(likes + 1);
            setLiked(true);
        }
    };

    return (
        <Container>
            <Image src={post.picture} alt="post" />
            <IconButton onClick={handleLike} color={liked ? 'secondary' : 'primary'}>
                <FavoriteIcon />
            </IconButton>
            <span>{likes}</span> {/* Display the number of likes */}
        </Container>
    );
}

export default DetailView;
