import { useState, useEffect, useContext } from 'react';
import { Box, Typography, styled, IconButton } from '@mui/material';
import { Favorite, Delete, Edit } from '@mui/icons-material';
import { Link, useNavigate, useParams } from 'react-router-dom';
import Like from './comments/Like';

import { API } from '../../service/api';
import { DataContext } from '../../context/DataProvider';
import Comments from './comments/Comments';

const Container = styled(Box)(({ theme }) => ({
    margin: '50px 100px',
    [theme.breakpoints.down('md')]: {
        margin: 0
    },
}));

const Image = styled('img')({
    width: '100%',
    height: '50vh',
    objectFit: 'cover'
});

const EditIcon = styled(Edit)`
    margin: 5px;
    padding: 5px;
    border: 1px solid #878787;
    border-radius: 10px;
`;

const DeleteIcon = styled(Delete)`
    margin: 5px;
    padding: 5px;
    border: 1px solid #878787;
    border-radius: 10px;
`;

const Heading = styled(Typography)`
    font-size: 38px;
    font-weight: 600;
    text-align: center;
    margin: 50px 0 10px 0;
`;

const Author = styled(Box)(({ theme }) => ({
    color: '#878787',
    display: 'flex',
    margin: '20px 0',
    [theme.breakpoints.down('sm')]: {
        display: 'block'
    },
}));

const DetailView = () => {
    const url = 'https://images.unsplash.com/photo-1543128639-4cb7e6eeef1b?ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8bGFwdG9wJTIwc2V0dXB8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80';

    const [post, setPost] = useState({});
    const [likes, setLikes] = useState(0); // New state for likes count
    const { account } = useContext(DataContext);

    const navigate = useNavigate();
    const { id } = useParams();

    const fetchData = async () => {
        const response = await API.getPostById(id);
        if (response.isSuccess) {
            setPost(response.data);
            setLikes(response.data.likes || 0); // Update likes count from the fetched post data
        }
    };

    useEffect(() => {
        fetchData();
    }, [id]);

    const handleLike = async () => {
        try {
            console.log('Attempting to like post...');
            const response = await API.likePost(post._id);
            console.log('Like response:', response); // Log the response
            if (response.isSuccess) {
                const updatedLikes = likes + 1;
                console.log('Updated likes:', updatedLikes); // Log the updated likes count
                setLikes(updatedLikes);
                setPost({ ...post, likes: updatedLikes });
            }
        } catch (error) {
            console.error('Error while liking the post:', error);
        }
    };

    const deleteBlog = async () => {
        await API.deletePost(post._id);
        navigate('/');
    };

    return (
        <Container>
            <Image src={post.picture || url} alt="post" />
            <Box style={{ float: 'right' }}>
                {account.username === post.username && (
                    <>
                        <Link to={`/update/${post._id}`}>
                            <EditIcon color="primary" />
                        </Link>
                        <DeleteIcon onClick={() => deleteBlog()} color="error" />
                    </>
                )}
            </Box>
            <Heading>{post.title}</Heading>

            <Author>
                <Link to={`/?username=${post.username}`} style={{ textDecoration: 'none', color: 'inherit' }}>
                    <Typography>
                        Author: <span style={{ fontWeight: 600 }}>{post.username}</span>
                    </Typography>
                </Link>
                <Typography style={{ marginLeft: 'auto' }}>{new Date(post.createdDate).toDateString()}</Typography>
            </Author>

            <Typography>{post.description}</Typography>
             
            <Typography>
                <span>
                    <IconButton onClick={handleLike} color="primary">
                        <Favorite />
                    </IconButton>
                </span>
                {likes} {/* Display the number of likes */}
            </Typography>

            <Like postId={post._id} updatePost={fetchData} /> {/* Pass fetchData function as prop to Like component */}

            <Comments post={post} />
        </Container>
    );
};

export default DetailView;
