import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Box, Typography, Button } from '@mui/material';
import { API } from '../../service/api';

const DetailView = () => {
    const { postId } = useParams();
    const [post, setPost] = useState(null);

    useEffect(() => {
        const fetchPost = async () => {
            const response = await API.getPostById(postId);
            setPost(response.data);
        };
        fetchPost();
    }, [postId]);

    const handleLike = async () => {
        await API.likePost(postId);
        // Update post with new like count
        const response = await API.getPostById(postId);
        setPost(response.data);
    };

    const handleDislike = async () => {
        await API.dislikePost(postId);
        // Update post with new dislike count
        const response = await API.getPostById(postId);
        setPost(response.data);
    };

    return (
        <Box>
            {post && (
                <Box>
                    <Typography variant="h5">{post.title}</Typography>
                    <Typography variant="body1">{post.description}</Typography>
                    <Typography variant="body2">Likes: {post.likes}</Typography>
                    <Typography variant="body2">Dislikes: {post.dislikes}</Typography>
                    <Button onClick={handleLike}>Like</Button>
                    <Button onClick={handleDislike}>Dislike</Button>
                </Box>
            )}
        </Box>
    );
};

export default DetailView;
